INSERT INTO `doctor` VALUES (63, NULL, 2, '梁瀛', '主任医师', '肺部结节，慢性气道疾病，呼吸道感染', '科研工作 主持多个国家级、省部级项目，发表SCI论文20余篇 学术兼职中华医学会呼吸病学分会慢阻肺学组委员中国老年医学学会呼吸病学分会呼吸生理与肺功能学组委员北京医学会呼吸病学分会慢阻肺学组委员兼工作秘书北京慢性病防治与健康教育研究会呼吸专业委员会委员北京防痨协会第十二届理事会理事北京大学医学部慢性气道疾病研究中心工作秘书北京市慢性肺部疾病学术联盟专家委员会常委获奖情况2023年获北京大学优秀共产党员2022年获北京医师协会第八届“...', '/static/images/doctor/doctor_63.jpg', 'https://puh3.net.cn/__local/4/87/60/DA320D3017873F18F1DBBC98159_9DC96F9A_42D13.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (64, NULL, 2, '王飞', '主任医师', '呼吸康复、慢性阻塞性肺疾病、支气管哮喘、慢性咳嗽等', '工作经历2022年8月至今，北京大学第三医院呼吸与危重医学科，主任医师；2013年8月2022年7月，北京大学第三医院呼吸与危重医学科，副主任医师；2007年8月至2013年7月，北京大学第三医院呼吸与危重医学科，主治医师；2016年1月至2017年1月，美国斯坦福大学医院院，访问学者。 科研工作主持国家自然科学基金青年基金及院内项目3项参与省部级研究项目3项发表论文20篇获批实用新型专利2项学术兼职中国康复医学会呼吸康复专业委员会青年委员会 ...', '/static/images/doctor/doctor_64.jpg', 'https://puh3.net.cn/__local/6/F8/C6/EA56CD456C18CCE515B5171FC09_EC139711_3EF2F.jpg', '2025-10-16 11:12:59');
