INSERT INTO `doctor` VALUES (66, NULL, 2, '盖晓燕', '主任医师、副教授', '呼吸系统疾病的诊断与治疗: （1）慢性气道疾病：慢阻肺、支气管哮喘、结核后慢性肺损伤等；（2）肺部疑难少见病诊治：如IgG4相关肺病、原发性纤毛不动综合征等。', '工作经历2024.8-至今 北京大学第三医院呼吸内科 主任医师 2021.8-至今 北京大学第三医院呼吸内科 副教授、硕士生导师 2019.8-2024.7 北京大学第三医院呼吸内科 副主任医师2011.8-2019.7 北京大学第三医院呼吸内科 主治医师 2006.8-2011.7 北京大学第三医院呼吸内科 住院医师 科研工作主要科研方向为慢性气流受限性疾病（如慢阻肺和结核后肺疾病）的临床与基础研究，获得国家及省部级项目资助多项。以第一或通讯作者发表SCI及中文核心期刊论文20余篇。...', '/static/images/doctor/doctor_66.jpg', 'https://puh3.net.cn/__local/9/A6/FB/A733C2996FEF633E175AD3873EC_6A4CD39C_50F6D.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (67, NULL, 2, '杜毅鹏', '副主任医师', '专业特长 慢性阻塞性肺疾病、支气管哮喘等呼吸系统疾病，呼吸危重症，介入呼吸病学', '科研工作 参与多项国家级及省部级课题的科研工作。在SCI及核心期刊上发表多篇论著，其中SCI第一作者2篇，核心期刊及统计源期刊第一作者2篇，并参与数本呼吸专业书籍的编写。 学术兼职 中国老年医学学会呼吸病学分会委员北京医学会呼吸内镜和介入学分会委员', '/static/images/doctor/doctor_67.jpg', 'https://puh3.net.cn/__local/A/F3/BF/D8E36652289B29F97AE021A0690_F3AEDBCB_42F2B.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (68, NULL, 2, '孙丽娜', '副主任医师', '慢性气道疾病；常见呼吸道感染性疾病', '工作经历2009年8月至2014年8月北京大学第三医院住院医师2014年9月至2020年8月北京大学第三医院主治医师2020年9月至今北京大学第三医院副主任医师科研工作承担国家自然科学基金青年科学基金项目1项学术兼职中国医师协会呼吸医师分会危重症医学工作委员会青年委员北京慢性病防治与健康教育研究会呼吸专委会秘书长获奖情况2016年获得中国医师协会、中华医学会、美国胸科医师协会共同认证的呼吸与危重症医学专业培训毕业证书2018年度北京大学第三医院优秀病案评选三等奖2例次2018年北京大学第三医院优秀教师2019年度北京大学第三医院疑难危重病例诊治奖评选一等奖2019年北京大学第三医院第四届住院医师、...', '/static/images/doctor/doctor_68.jpg', 'https://puh3.net.cn/__local/0/D5/CB/BC2A56744E5AC176700BF063A0F_FF66BF37_45DA3.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (69, NULL, 3, '洪天配', '主任医师、教授', '糖尿病、甲状腺疾病、内分泌代谢疑难病等。', '工作经历 1991年毕业于北京大学医学部内分泌代谢专业获医学博士学位，毕业后一直在北京大学第三医院内分泌科工作。曾在丹麦Steno糖尿病中心从事客座研究两年。1993年被聘为副主任医师、副教授、硕士生导师。2001年被聘为主任医师、教授、博士生导师。2009年被评为北京大学二级教授。2003年起担任现任北京大学第三医院内分泌科主任、内分泌研究室主任。2014年至2018年兼任北京大学第三医院检验科主任。牵头制订中华人民共和国卫生行业标准《...', '/static/images/doctor/doctor_69.jpg', 'https://puh3.net.cn/__local/3/D1/5B/03F171C88E004CBF344A482E46C_A277A96A_19B6B.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (70, NULL, 3, '李琼芳', '主任医师、研究员', '糖尿病、甲状腺病等内分泌疾病', '科研工作 主要学术研究方向为2型糖尿病的诊治研究和分子遗传学研究。曾主持和参与完成卫生部科学研究基金、美国纽约中华医学基金等课题多项。 学术兼职 1934年5月出生。现职称：主任医师、研究员。专业特长：糖尿病、甲状腺病等内分泌疾病。1959年毕业于北京医学院。毕业后分配在中国医学科学院任实习研究员。1972年2月调入北医三院内科任助理研究员。1989年2月至1990年2月赴美国底特律哈泼（HAPPER）医院做访问学者。...', '/static/images/doctor/doctor_70.jpg', 'https://puh3.net.cn/__local/5/F4/7C/8E07EBCF3D6D0D6CF5351AA53AF_E7903325_6044.jpg?e=.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (72, NULL, 3, '高洪伟', '主任医师', '糖尿病、甲状腺疾病、内分泌代谢疑难病', '工作经历 1990年毕业于北京大学医学部，获医学学士学位，毕业后一直在北京大学第三医院内分泌科工作至今。1996年获医学硕士学位，2008年获医学博士学位。2001年在英国Keele大学生命科学学院从事客座研究一年。2002年被聘为副主任医师，2003年聘为副教授、硕士生导师。2008年被聘为主任医师。2006年起担任现任北京大学第三医院内分泌科副主任。 科研工作 主要研究方向是糖尿病基础与临床研究、内分泌代谢与生殖健康研究。...', '/static/images/doctor/doctor_72.jpg', 'https://puh3.net.cn/__local/B/87/76/D6BCD89008FA3FB72BE1261FF33_DAB2FA6B_2B233.jpg?e=.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (73, NULL, 3, '王群', '副主任护师、糖尿病专科护士', '临床护理、护理教育、护理管理、糖尿病教育与管理', '科研工作 临床护理和糖尿病教育与管理 学术兼职 北京护理学会内分泌专业委员会 主任委员中华护理学会糖尿病专业委员会 副主任委员中华医学会糖尿病分会教育与管理学组 委员中国健康促进与教育协会糖尿病与管理分会 委员 论文 1 王群.糖尿病足患者足部护理知识和足部自护行为状况的调查分析,护理管理杂志,2013,3(13):179-1802王群.护士分层管理与分类教育模式在糖尿病健康教育中的应用, 护理学杂志,2012,19,27(19)...', '/static/images/doctor/doctor_73.jpg', 'https://puh3.net.cn/__local/4/DD/78/B770A72A9B3CD391C3974476795_5791F012_22435.jpg?e=.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (74, NULL, 3, '王艳荣', '副主任医师', '糖尿病、甲状腺疾病及肥胖相关疾病', '王艳荣 基本情况 姓名：王艳荣 性别：女 学位：博士 毕业院校：乌克兰国立医科大学 专业：内分泌与代谢病 技术职称：副主任医师 导师资格：硕士生导师 专业特长...', '/static/images/doctor/doctor_74.jpg', 'https://puh3.net.cn/__local/3/35/7B/7379DAA60711ADD64349824AD7F_CEC352BB_54F0.jpg?e=.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (75, NULL, 3, '雷天光', '副主任医师', '糖尿病、甲状腺疾病', '工作经历 兰州大学第二医院主治医师 北京大学第三医院主治医师 美国波士顿大学医学院 Research Associate. 北京大学第三医院内分泌科副主任医师 科研工作主要从事糖尿病及甲状腺疾病的诊断和治疗相关研究，发表SCI论文及中文核心论文十余篇。', '/static/images/doctor/doctor_75.jpg', 'https://puh3.net.cn/__local/9/91/11/C8B5AF62B5522AA0651B0D99736_4959421F_305CF.jpg?e=.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (76, NULL, 3, '肖文华', '主任医师', '糖尿病、甲状腺疾病等', '工作经历 1994年9月～1999年10月 北医三院内科 住院医师 1999年11月～2000年9月 北医三院内科 住院总医师 2000年10月～2009年7月 北医三院内分泌科 主治医师 2009年8月~2021年7月 北医三院内分泌科 副主任医师 2021年8月~至今 北医三院内分泌科 主任医师 2014年8月~至今 北医三院大内科 副主任 2019年8月~至今 北医三院内分泌科 副主任 科研工作 甲状腺疾病、骨代谢疾病等内分泌科疾病 学术兼职 中华医学会内分泌学分会骨代谢组、...', '/static/images/doctor/doctor_76.jpg', 'https://puh3.net.cn/__local/9/4E/37/820695B780577A9693990808F80_72FF95D0_2F911.jpg?e=.jpg', '2025-10-16 11:12:59');
INSERT INTO `doctor` VALUES (77, NULL, 3, '王海宁', '主任医师', '糖尿病、甲状腺疾病、内分泌疾病与生殖健康等。', '工作经历 2006年毕业于北京大学医学部内分泌代谢专业获医学博士学位，毕业后一直在北京大学第三医院内分泌科工作。曾在丹麦Steno糖尿病中心访问学者，芬兰赫尔辛基大学公共卫生学院 访问学者。2019年被聘为主任医师 科研工作 主要研究方向是糖尿病基础与临床研究、内分泌代谢与生殖健康研究、临床流行病学研究等。先后参与国家级和省部级科研课题20余项，主持国家自然科学基金2项，中华医学和会基金1项[1] 国家自然基金委面上项目负责人：...', '/static/images/doctor/doctor_77.jpg', 'https://puh3.net.cn/__local/4/E8/39/FDF8F6590374AEC56EAF76429DB_04C473C3_19653.jpg?e=.jpg', '2025-10-16 11:12:59');
