INSERT INTO `doctor` VALUES (178, NULL, 9, '谢茹萍', '主任医师', '中枢神经系统脱髓鞘疾病，神经系统疑难病，脑血管疾病', '学术兼职 曾任北京市中西医结合学会第四届、五届神经科专业委员会副主任委员，任北京电视台（电视门诊）栏目医学顾问及东方医网及中国医疗保健信息网学术指导顾问，“中华中西医杂志”编委，天坛医院神经内科会诊中心专家组成员。 获奖情况 1987年和1992年曾两次被评为北京医科大学临床医师教学优秀个人奖', '/static/images/doctor/doctor_178.jpg', 'https://puh3.net.cn/__local/C/EA/7E/92611D69E4487E58BB46004DB3F_39A26CDA_22B4A.jpg?e=.jpg', '2025-10-16 11:12:59');
