INSERT INTO `doctor` VALUES (754, NULL, 37, '韩悦', '主治医师', '高血压、糖尿病、冠心病、慢性肾功能不全、脑血管病等慢性病的管理和诊治。', '北京大学医学部全科医学专业硕士，擅长高血压、糖尿病、冠心病、慢性肾功能不全、脑血管病等慢性病的管理和诊治。', '/static/images/doctor/doctor_754.jpg', 'https://puh3.net.cn/__local/0/00/2C/8A2701482A585F1B9CA8ABF806F_558D927C_14D3B.jpg', '2025-10-16 11:12:59');
