DROP TABLE IF EXISTS `user`;
CREATE TABLE `user`  (
  `user_id` int NOT NULL AUTO_INCREMENT COMMENT '内部主键',
  `phonenumber` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '手机号,患者端唯一登入凭证',
  `identifier` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '学号或工号，用于医生/管理端登入 以及学生/教师认证信息',
  `user_type` enum('student','teacher','doctor','admin','external') CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'external' COMMENT '用户身份类型：学生/教师/管理员等',
  `email` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '允许不强制使用邮箱',
  `hashed_password` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '哈希密码',
  `create_time` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `is_active` tinyint(1) NOT NULL DEFAULT 1 COMMENT '用户是否有效(可被封禁)',
  `is_deleted` tinyint(1) NOT NULL DEFAULT 0 COMMENT '用户是否被删除',
  `is_admin` tinyint(1) NOT NULL DEFAULT 0 COMMENT '用户是否是超级管理员',
  `is_verified` tinyint(1) NOT NULL DEFAULT 0 COMMENT '邮箱/手机号/身份信息是否通过验证',
  `last_login_ip` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '最近登录IP',
  `last_login_time` bigint NULL DEFAULT NULL COMMENT '最近登录时间（时间戳）',
  PRIMARY KEY (`user_id`) USING BTREE,
  UNIQUE INDEX `phone_number`(`phonenumber` ASC) USING BTREE,
  UNIQUE INDEX `phone_number_2`(`phonenumber` ASC) USING BTREE,
  UNIQUE INDEX `phone_number_3`(`phonenumber` ASC) USING BTREE,
  UNIQUE INDEX `email`(`email` ASC) USING BTREE,
  UNIQUE INDEX `identifier`(`identifier` ASC) USING BTREE,
  UNIQUE INDEX `email_2`(`email` ASC) USING BTREE,
  UNIQUE INDEX `email_3`(`email` ASC) USING BTREE,
  UNIQUE INDEX `phonenumber`(`phonenumber` ASC) USING BTREE,
  UNIQUE INDEX `identifier_2`(`identifier` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 13 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '所有用户的登录认证和权限主表' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of user
-- ----------------------------

