DROP TABLE IF EXISTS `visit_history`;
CREATE TABLE `visit_history`  (
  `visit_id` bigint NOT NULL AUTO_INCREMENT COMMENT '就诊记录ID',
  `patient_id` bigint NOT NULL COMMENT '关联 patient.patient_id',
  `doctor_id` int NULL DEFAULT NULL COMMENT '关联 doctor.doctor_id',
  `schedule_id` bigint NULL DEFAULT NULL COMMENT '可选：关联排班',
  `visit_date` date NOT NULL COMMENT '就诊日期',
  `diagnosis` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '医生诊断结论',
  `advice` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '医生建议/处置',
  `prescription` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '处方/药品清单 (文本或 JSON 字符串)',
  `attachments` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '附件路径或描述(JSON)',
  `followup_required` tinyint(1) NULL DEFAULT NULL COMMENT '是否需要复诊',
  `followup_date` date NULL DEFAULT NULL COMMENT '建议复诊日期',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime NULL DEFAULT NULL COMMENT '最后更新时间',
  PRIMARY KEY (`visit_id`) USING BTREE,
  INDEX `patient_id`(`patient_id` ASC) USING BTREE,
  INDEX `doctor_id`(`doctor_id` ASC) USING BTREE,
  INDEX `schedule_id`(`schedule_id` ASC) USING BTREE,
  CONSTRAINT `visit_history_ibfk_1` FOREIGN KEY (`patient_id`) REFERENCES `patient` (`patient_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `visit_history_ibfk_2` FOREIGN KEY (`doctor_id`) REFERENCES `doctor` (`doctor_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `visit_history_ibfk_3` FOREIGN KEY (`schedule_id`) REFERENCES `schedule` (`schedule_id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = DYNAMIC;

